<script>
import { defineComponent, onMounted, ref } from '@nuxtjs/composition-api';
import * as d3 from 'd3';

export default defineComponent({
  props: {
    title: {
      type: String,
      required: true,
    },
  },
  setup() {
    // template refs
    const Johor = ref(null);
    const Kedah = ref(null);
    const Kelantan = ref(null);
    const Melaka = ref(null);
    const NegeriSembilan = ref(null);
    const Pahang = ref(null);
    const Perak = ref(null);
    const Perlis = ref(null);
    const PulauPinang = ref(null);
    const Sabah = ref(null);
    const Sarawak = ref(null);
    const Selangor = ref(null);
    const Terengganu = ref(null);
    // territory
    const KualaLumpur = ref(null);
    // // territory
    // const Labuan = ref(null);
    // // territory
    // const Putrajaya = ref(null);
    const summary = ref(null);
    const tooltip = ref(null);

    const states = [
      {
        shortName: 'JHR',
        name: 'Johor',
        ref: Johor,
        target: 150,
      },
      {
        shortName: 'KED',
        name: 'Kedah',
        ref: Kedah,
        target: 150,
      },
      {
        shortName: 'KEL',
        name: 'Kelantan',
        ref: Kelantan,
        target: 150,
      },
      {
        shortName: 'MLK',
        name: 'Melaka',
        ref: Melaka,
        target: 150,
      },
      {
        shortName: 'N9',
        name: 'Negeri Sembilan',
        ref: NegeriSembilan,
        target: 150,
      },
      {
        shortName: 'PAH',
        name: 'Pahang',
        ref: Pahang,
        target: 150,
      },
      {
        shortName: 'PRK',
        name: 'Perak',
        ref: Perak,
        target: 150,
      },
      {
        shortName: 'PER',
        name: 'Perlis',
        ref: Perlis,
        target: 150,
      },
      {
        shortName: 'PG',
        name: 'Pulau Pinang',
        ref: PulauPinang,
        target: 150,
      },
      {
        shortName: 'SBH',
        name: 'Sabah',
        ref: Sabah,
        target: 150,
      },
      {
        shortName: 'SAR',
        name: 'Sarawak',
        ref: Sarawak,
        target: 150,
      },
      {
        shortName: 'SEL',
        name: 'Selangor',
        ref: Selangor,
        target: 150,
      },
      {
        shortName: 'TER',
        name: 'Terengganu',
        ref: Terengganu,
        target: 150,
      },
      {
        shortName: 'WP',
        name: 'Kuala Lumpur',
        ref: KualaLumpur,
        target: 150,
      },
      // {
      //   shortName: 'Labuan',
      //   ref: false,
      //   target: 150
      // },
      // {
      //   shortName: 'Putrajaya',
      //   ref: false,
      //   target: 150
      // }
    ];

    onMounted(() => {
      const hover = d3.select(tooltip.value);

      states.forEach((state) => {
        const rgbFill = d3
          .scaleLinear()
          .domain([0, state.target])
          .range(['rgb(229, 245, 224)', 'rgb(0, 109, 44)'])
          .clamp(true);

        const value = Math.random() * 200;

        d3.select(state.ref.value)
          .style('fill', rgbFill(value))
          .on('mouseover', (d) => {
            hover
              .html(
                `
                <p><strong>${state.name}</strong></p>
                <p>Value: ${value.toFixed(2)}</p>
                <p>Target: ${state.target}</p>
                `
              )
              .style('left', d.clientX + 'px')
              .style('top', d.clientY + 'px')
              .style('opacity', 0.9);
          })
          .on('mousemove', (d) => {
            hover
              .style('left', d.clientX + 10 + 'px')
              .style('top', d.clientY + 10 + 'px');
          })
          .on('mouseout', () => {
            hover
              // .transition()
              // .duration(500)
              .style('opacity', 0);
          });
      });

      // Perlis.value.style.fill = 'red';
      // Putrajaya.value.style.fill = 'red';
    });

    return {
      Johor,
      Kedah,
      Kelantan,
      Melaka,
      NegeriSembilan,
      Pahang,
      Perak,
      Perlis,
      PulauPinang,
      Sabah,
      Sarawak,
      Selangor,
      Terengganu,
      KualaLumpur,
      // Labuan,
      // Putrajaya,
      summary,
      tooltip,
    };
  },
});
</script>

